mapper.insert:
INSERT INTO qna VALUES((SELECT NVL(MAX(qna_no)+1,1) FROM qna),
 #{msg},SYSDATE,#{cate},#{group_id},#{tab},#{id}
)

mapper.select:
SELECT qna_no,msg,TO_CHAR(regdate,'YYYY-MM-DD:HH24:MI:SS') as dbday,cate,group_id,tab,name,num
FROM (SELECT qna_no,msg,regdate,cate,group_id,tab,name,rownum as num
FROM (SELECT qna_no,msg,qna.regdate as regdate,cate,group_id,tab,member.name as name
FROM qna,member 
WHERE qna.id=member.id
AND tab=#{tab}
AND pno=#{pno}
AND cate=#{cate}
ORDER BY group_id))
WHERE num BETWEEN #{start} AND #{end}

   SELECT qna_no,msg,TO_CHAR(qna.regdate,'YYYY-MM-DD:HH24:MI:SS') as dbday,cate,group_id,tab,member.name as name
    FROM qna,member
	WHERE qna.id=member.id
	AND qna.group_id=#{group_id}
	AND tab=#{tab}

SELECT COUNT(*) FROM qna 
WHERE cate=#{cate}
AND pno=#{pno}
