mapper.update:
UPDATE hall SET
wishcount=(SELECT COUNT(*) FROM wish_list WHERE cate=#{cate} AND cno=#{cno})
WHERE hno=#{cno}

UPDATE studio SET
wish=(SELECT COUNT(*) FROM wish_list WHERE cate=#{cate} AND cno=#{cno})
WHERE sno=#{cno}

UPDATE hair_makeup SET
wish=(SELECT COUNT(*) FROM wish_list WHERE cate=#{cate} AND cno=#{cno})
WHERE mno=#{cno}
